using System;
using UnityEngine;

namespace NeonFolders.Editor {
	/// <summary>
	/// Provides static resources like encoded images.
	/// </summary>
	public static class Resources {
		public static readonly Base64Texture EditFolder64 = new Base64Texture("");
		public static readonly Base64Texture EditFolder16 = new Base64Texture("");
		public static readonly Base64Texture RuleSetIcon = new Base64Texture("");

		private static Texture2D Decode(string base64) {
			var texture = new Texture2D(2, 2);
			try {
				var png = Convert.FromBase64String(base64);
				texture.LoadImage(png);
			} catch(Exception e) {
				Debug.LogException(e);
			}
			return texture;
		}

		/// <summary>
		/// Represents a texture which is lazily loaded from a base64 encoded png.
		/// </summary>
		public class Base64Texture {
			private readonly string base64;
			private Texture2D texture;

			public Texture2D Texture { get { return texture ?? (texture = Decode(base64)); } }

			public Base64Texture(string base64) {
				this.base64 = base64;
			}

			public static implicit operator Texture2D(Base64Texture base64) {
				return base64.Texture;
			}
		}
	}
}
